import React, { useState } from 'react'
import Axios from 'axios'

const FormPost = () => {


    const [title, setTitle] = useState('')
    const [body, setBody] = useState('')
    const [errorTitle, setErrorTitle] = useState(false)
    const [errorBody, setErrorBody] = useState(false)

    const agregar = async (e) => {

        e.preventDefault()

        setErrorTitle(false)
        setErrorBody(false)

        if(!title.trim() || !body.trim()){

            if(!title.trim()){
                setErrorTitle(true)
            }

            if(!title.trim()){
                setErrorBody(true)
            }
            return 
        } 

        try {
            Axios.post('https://jsonplaceholder.typicode.com/posts', {
                title: title,
                body: body
            }).then(res => {
                // Respuesta del servidor
                console.log("post agregado")
                setErrorTitle(false)
                setErrorBody(false)
            }).catch(e => {
                console.log(e);
            });
        } catch (err){
            console.log(err)
        }

    }

    return (
        <div className="my-5">
            <h2>Agregar Post</h2>
            <form onSubmit={agregar}>
                <label htmlFor="title" className="col-form-label-lg">Titulo</label>
                <input 
                    type="text" 
                    className="form-control" 
                    onChange={e => setTitle(e.target.value)}
                    value={title}
                />
                {
                    errorTitle? <h3 className="text-danger">Complete el t√≠tulo</h3> : null
                }
                <label htmlFor="body" className="col-form-label-lg">Contenido</label>
                <input 
                    type="text" 
                    className="form-control" 
                    onChange={e => setBody(e.target.value)}
                    value={body}
                />
                {
                    errorBody? <h3 className="text-danger">Complete el contenido</h3> : null
                }
                <input type="submit" className="btn btn-primary mt-4 float-right" value="Enviar"/>
            </form>
        </div>
    )
}

export default FormPost
