import Axios from 'axios'

export const getPost = async (id) => {
    console.log(id)

    var PostX = {
        Title: '',
        Body: ''
    }

    try {
        Axios.get(`https://jsonplaceholder.typicode.com/posts/${id}`)
            .then((res) => {
                PostX = res.data;

                console.log(res.data);
            })
            .catch(err => {
                PostX.Title = "No existe el post";
                console.log(err)
            })
    }
    catch(err) {
        console.log(err)
    }

    return ( 
        {
            PostX
        }
    )
}
