import React, { useEffect, useState } from 'react'

import DeleteButton from './Home/DeleteButton'
import EditButton from './Home/EditButton'
import DetailButton from './Home/DetailButton'

import getPosts from '../services/getPosts'
import FormPost from './FormPost'

const EditForm = () => {

    const [Posts, setPosts] = useState([]);

    useEffect(() => {

        getPosts().then(postX => {
            setPosts(postX)
        })
        .catch(err => {
            console.log(err)
        })


    }, [])

    const handleClick = (idItem) => {
        id = idItem
    }


    return (
        <div className="my-3">

            <h2 className="">Posts</h2>

            <div className="form-floating">
                <select name="select" id="floatingSelect" className="form-select">

                {
                    Posts.map(item => (

                        <option value="value1" onChange={handleClick(item.id)}>{item.title}</option>


                        /* {
                        <li className="list-group-item" key={item.id}>
                            <div className="row">
                                <div className="col-2 mt-2">
                                    <h5>ID: {item.id}</h5>
                                </div>
                                <div className="col-10">
                                    <h3 className="mb-4">{item.title}</h3>
                                    <DeleteButton item={item.id}/>

                                    <EditButton item={item.id}/>
                                    <DetailButton item={item.id}/>

                                </div>
                            </div>


                        </li>} */
                    ))
                }
                </select>
                <EditButton item={id}/>
                <label for="floatingSelect">Works with selects</label>
            </div>


        </div>
    )
}

export default EditForm
