                Axios.get(`https://jsonplaceholder.typicode.com/posts/${id}`)
                    .then((res) => {
                        const post = {
                            title: res.title,
                            body: res.body
                        }
                        console.log(res.data);
                    })
                    .catch(err => {
                        console.log(err)
                    })
            }
            catch(err) {
                console.log(err)
            }
        }

        getPost()

    }, [])


    return (
        <Fragment>
            <h1>Hola mundo</h1>
            <h3>ID {id}</h3>
            <h3>{currentPath}</h3>

        </Fragment>
    )
}

export default Post
